import { Ingredient } from '../../shared/ingredient.model';
import * as ShoppingListActions from './shopping-list.actions';
const initialState = {
  ingredients: [new Ingredient('Apples', 5), new Ingredient('Tomatoes', 10)],
  editedIngredient: null,
  editedIngredientIndex: -1
};
export function shoppingListReducer(state = initialState, action) {
  console.log('ShoppingList Action => ', action.type);
  console.log('ShoppingList Reducer: Action Payload => ', action.payload);
  console.log('Current State => ', state);

  switch (action.type) {
    case ShoppingListActions.ADD_INGREDIENT:
      return Object.assign(Object.assign({}, state), {
        ingredients: [...state.ingredients, action.payload]
      });

    case ShoppingListActions.ADD_INGREDIENTS:
      console.log('Modified state => ', Object.assign(Object.assign({}, state), {
        ingredients: [...state.ingredients, ...action.payload]
      }));
      return Object.assign(Object.assign({}, state), {
        ingredients: [...state.ingredients, ...action.payload]
      });

    case ShoppingListActions.UPDATE_INGREDIENTS:
      const ingredient = state.ingredients[action.payload.index];
      const updatedIngredient = Object.assign(Object.assign({}, ingredient), action.payload.ingredient);
      const updatedIngredients = [...state.ingredients];
      updatedIngredients[action.payload.index] = updatedIngredient;
      console.log('Modified state => ', Object.assign(Object.assign({}, state), {
        ingredients: updatedIngredients
      }));
      return Object.assign(Object.assign({}, state), {
        ingredients: updatedIngredients
      });

    case ShoppingListActions.DELETE_INGREDIENT:
      const currentIngredients = state.ingredients;
      const filteredIngredients = currentIngredients.filter((ingredient, index) => index !== action.payload);
      console.log('Modified State => ', filteredIngredients);
      return Object.assign(Object.assign({}, state), {
        ingredients: filteredIngredients
      });

    case ShoppingListActions.START_EDIT:
      return Object.assign(Object.assign({}, state), {
        editedIngredientIndex: action.payload,
        editedIngredient: state.ingredients[action.payload]
      });

    case ShoppingListActions.STOP_EDIT:
      return Object.assign(Object.assign({}, state), {
        editedIngredient: null,
        editedIngredientIndex: -1
      });

    default:
      return state;
  }
} // There is another bug created for the same issue that the workflow history table is not updating after Re-route. Once that bug is fixed, this issue will be resolved
