import { Ingredient } from '../../shared/ingredient.model';
import * as ShoppingListActions from './shopping-list.actions'; // Reducer takes 2 arguments - Current State, Action

const initialState = {
  ingredients: [new Ingredient('Apples', 5), new Ingredient('Tomatoes', 10)]
}; // state = initialState (Default value)

export function shoppingListReducer(state = initialState, action) {
  console.log('ShoppingList Reducer: Modify Current state => ', action.payload);

  switch (action.type) {
    case ShoppingListActions.ADD_INGREDIENT:
      return Object.assign(Object.assign({}, state), {
        ingredients: [...state.ingredients, action.payload]
      });

    case ShoppingListActions.ADD_INGREDIENTS:
      return Object.assign(Object.assign({}, state), {
        ingredient: [...state.ingredients, ...action.payload]
      });

    default:
      return state;
  }
}
